sys_script_client.script:
function onLoad() {
    var status = g_form.getValue("status");
    var inPreparation = (status == "in_preparation");
    var pendingCoordinator = (status == "pending_coordinator");
    var pendingGeneralDirector = (status == "pending_general_director");
    var pendingCabinet = (status == "pending_cabinet");
    var adopted = (status == "adopted");
    var abandoned = (status == "abandoned");


    var isAuthor = g_user.hasRole('x_378151_decide.author');
    var isCoordinator = g_user.hasRole('x_378151_decide.coordinator');
    var isDirector = g_user.hasRole('x_378151_decide.director');
    var isCabinet = g_user.hasRole('x_378151_decide.cabinet');
    var isNewRecord = g_form.isNewRecord();

    if (isNewRecord) {
        g_form.setValue('main_author_dossier', g_user.getUserID());
        g_form.setValue('main_author_document', '62826bf03710200044e0bfc8bcbe5df1'); // abel tuter
        if (isCoordinator) {
            g_form.setValue('status', "pending_coordinator");
            g_form.setValue('main_legislative_coordinator', g_user.getUserID())
        }
        setVisible(false);
    } else {
        // HIDE MAIN CONTACTS 
        g_form.setDisplay('main_author_dossier', false);
        g_form.setDisplay('main_author_document', false);
        g_form.setDisplay('main_legislative_coordinator', false);
        setVisible(true);
        //if (isDirector || isCabinet) setReadOnly(true); else 
       if ((pendingCoordinator && !isCoordinator) || (pendingGeneralDirector && !isDirector) 
			|| pendingCabinet || abandoned || adopted) 
			setReadOnly(true);
    }

    function setVisible(visible) {
        g_form.setDisplay('duration_of_the_procedure', visible);
        g_form.setDisplay('consultation_details', visible);
        g_form.setDisplay('justification_for_the_adoption', visible);
        g_form.setDisplay('politically_sensitive_important', visible);
        g_form.setDisplay('requested_adoption_date', visible);
        g_form.setDisplay('short_title', visible);
        g_form.setDisplay('full_title', visible);
        g_form.setDisplay('type_of_signature_visa', visible);
        g_form.setDisplay('confidentiality_level', visible);
        g_form.setSectionDisplay('finality', visible);
        g_form.setSectionDisplay('director_generalvisa', visible);
        g_form.setSectionDisplay('notes_history', visible);
        g_form.setSectionDisplay('info_sggreffe', visible);

    }

    function setReadOnly(readOnly) {
        g_form.setReadOnly('procedure_type', readOnly);
        g_form.setReadOnly('duration_of_the_procedure', readOnly);
        g_form.setReadOnly('consultation_details', readOnly);
        g_form.setReadOnly('justification_for_the_adoption', readOnly);
        g_form.setReadOnly('politically_sensitive_important', readOnly);
        g_form.setReadOnly('requested_adoption_date', readOnly);
        g_form.setReadOnly('short_title', readOnly);
        g_form.setReadOnly('full_title', readOnly);
        g_form.setReadOnly('main_author_dossier', readOnly);
        g_form.setReadOnly('main_author_document', readOnly);
        g_form.setReadOnly('main_legislative_coordinator', readOnly);
        g_form.setReadOnly('type_of_signature_visa', readOnly);
        g_form.setReadOnly('confidentiality_level', readOnly);
    }
}
