sys_script_include.script:
var decideUtils = Class.create();
decideUtils.prototype = {
    initialize: function() {},
    isVisibleRequestCabinetAgreement: function(current) {
        var isCoordinator = gs.hasRole('x_378151_decide.coordinator');
        var pendingCoordinator = (current.status == "pending_coordinator");
        return isCoordinator && pendingCoordinator;
    },
    isVisibleSignRejectDossier: function(current) {
        var isDirector = gs.hasRole('x_378151_decide.director');
        var pendingGeneralDirector = (current.status == "pending_general_director");
        return isDirector && pendingGeneralDirector;
    },
    isVisibleReadyForCoordinator: function(current) {
        var isAuthor = gs.hasRole('x_378151_decide.author');
        var inPreparation = (current.status == "in_preparation");
        return isAuthor && inPreparation;
    },
    isVisibleAbandonDossier: function(current) {
        var isAuthor = gs.hasRole('x_378151_decide.author');
        var inPreparation = (current.status == "in_preparation");
        var isDirector = gs.hasRole('x_378151_decide.director');
        var pendingGeneralDirector = (current.status == "pending_general_director");
        var isCoordinator = gs.hasRole('x_378151_decide.coordinator');
        var pendingCoordinator = (current.status == "pending_coordinator");
        var isCabinet = gs.hasRole('x_378151_decide.cabinet');
        var pendingCabinet = (current.status == "pending_cabinet");
        return ((isAuthor && inPreparation) || 
				(isDirector && pendingGeneralDirector) || 
				(isCoordinator && pendingCoordinator) || 
				(isCabinet && pendingCabinet));
    },
	isVisibleAdoptDossier: function(current) {
        var isCabinet = gs.hasRole('x_378151_decide.cabinet');
        var pendingCabinet = (current.status == "pending_cabinet");
        return (isCabinet && pendingCabinet);
    },
    type: 'decideUtils'
};
