sys_ui_action.script:
function requestCabinetAgreement() {
	if(g_form.getValue('comments') == ''){
      g_form.setMandatory('comments', true);
      g_form.showFieldMsg('comments','Please add a message in the Additional comments.','error');
      return false;  //Abort submission

   }

    var isAuthor = g_user.hasRole('x_378151_decide.author');
    var isCoordinator = g_user.hasRole('x_378151_decide.coordinator');
    var isDirector = g_user.hasRole('x_378151_decide.director');
    var status = g_form.getValue("status");
    var inPreparation = (status == "in_preparation");
    var pendingCoordinator = (status == "pending_coordinator");
    var pendingGeneralDirector = (status == "pending_general_director");
    var electronicVisa = (g_form.getValue("type_of_signature_visa") == "electronic_via_decide");

    if (inPreparation && isAuthor) {
        g_form.setValue("status", "pending_coordinator");
    } else if (inPreparation && isCoordinator) {
        g_form.setValue("status", "pending_cabinet");
    } else if (pendingCoordinator && isCoordinator && electronicVisa) {
        g_form.setValue("status", "pending_general_director");
    } else if (pendingCoordinator && isCoordinator && !electronicVisa) {
        g_form.setValue("status", "pending_cabinet");
    } else if (pendingGeneralDirector && isDirector) {
        g_form.setValue("status", "pending_cabinet");
    }
    gsftSubmit(null, g_form.getFormElement(), "button_request_cabinet_agreement");
}
//Code that runs triggered by the 'onclick' function in gsftSubmit on the Server Side
if (typeof window == 'undefined')
    serverReopen();

function serverReopen() {
    current.update();
	if (current.type_of_signature_visa == "electronic_via_decide")
		gs.addInfoMessage('A notification will be sent to the director');
	else
		gs.addInfoMessage('A notification will be sent to the cabinet members ');
    action.setRedirectURL(current);
}
